#设置客户端连接的编码
SET NAMES UTF8;
#丢弃数据库
DROP DATABASE IF EXISTS yijia;
#创建数据库
CREATE DATABASE yijia CHARSET=UTF8;
#进入数据库
USE  yijia;
#创建保存用户数据的表
CREATE TABLE yijia_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(32), #用户名
    upwd VARCHAR(20),  #密码
    gender BOOL,       #性别 0女 1男
    birthday DATE,     
    user_name VARCHAR(32),  #真实姓名
    email VARCHAR(32),
    phone VARCHAR(11),
    home VARCHAR(64),
    city VARCHAR(64),
    hobby VARCHAR(128)
);


#创建首页轮播图
CREATE TABLE yijia_index_carousel(
    cid INT PRIMARY KEY AUTO_INCREMENT,
    img VARCHAR(128)
);


#创建保存地区房源信息的表
CREATE TABLE yijia_house(
    hid INT PRIMARY KEY AUTO_INCREMENT,
    hname VARCHAR(64),  #房屋名
    title VARCHAR(128),  #标题
    detail VARCHAR(256), #详情
    hic VARCHAR(128),    #大图片
    price DECIMAL(6,2),  #价格
    cid INT,             #市
    aid INT,             #县 
    addr_detail VARCHAR(256),  #详细地址
    h_grade VARCHAR(64),        #房屋级别  
    h_layout VARCHAR(32),  #户型
    Layout VARCHAR(64),  #房型 
    #配套  0没有 1有
    WIFI BOOL,             #无线网络
    hot_water BOOL,        #全天热水
    elevator BOOL,         #电梯     
    washing_machine BOOL,  #洗衣机
    TV BOOL,               #电视
    air_conditioning BOOL,  #空调
    refrigerator BOOL,     #冰箱
    microwave_oven  BOOL,   #微波炉   
    #特色
    cooking BOOL,           #做饭
    long_term_lease BOOL,   #长租
    con_pre BOOL,           #连住优惠        
    pet BOOL,               #宠物同行
    party BOOL,             #聚会轰趴
    ocean BOOL,             #海景房
    lake BOOL,              #湖景房
    river BOOL,             #江景房
    mountain BOOL,          #山景房
    #其他  0没有 1有
    mansion BOOL,           #豪宅
    landlord BOOL,          #超赞房东
    new_house BOOL,         #超赞新房
    autarky BOOL,           #自营民宿    
    separate_room BOOL,     #独立单间    
    h_select BOOL,          #艺家优选
    receipt BOOL,           #提供发票
    vacation_package BOOL,   #度假套餐
    #判断
    chara BOOL 
);


#创建保存房源分类总菜单的表
CREATE TABLE pri_nav(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    type_name VARCHAR(64)
);


#创建保存房源分类二级菜单的表
CREATE TABLE sec_nav(
    sid INT PRIMARY KEY AUTO_INCREMENT,
    type_name VARCHAR(64),
    pid INT    #外键pri_nav的pid
);

#创建保存市的表
CREATE TABLE city(
    cid INT PRIMARY KEY AUTO_INCREMENT,
    cname VARCHAR(64),
    initial VARCHAR(16)
);


#创建保存区或县的表
CREATE TABLE area(
    aid INT PRIMARY KEY AUTO_INCREMENT,
    aname VARCHAR(64),
    cid INT   #外键city表的cid
);

#创建保存房源信息图的表
CREATE TABLE h_img(
    iid INT PRIMARY KEY AUTO_INCREMENT,
    hic VARCHAR(128),  #房源图片路径
    hid INT            #外键yijia_house表的hid
);

#创建保存首页底部轮播切换的表
CREATE TABLE change_banner(
    cid INT PRIMARY KEY AUTO_INCREMENT,
    cic VARCHAR(128)
);

#创建保存设施服务的表
CREATE TABLE facility_service(
    fid INT PRIMARY KEY AUTO_INCREMENT,
    infr VARCHAR(64),
    sani VARCHAR(64),
    galley VARCHAR(64),  
    surr VARCHAR(64),
    item VARCHAR(64),
    serv VARCHAR(64)
);

